mapper.select:
select user_id, activity_id, strategy_id, order_id, award_id, award_title, award_time,
       award_state, create_time, update_time
from user_award_record
where user_id = #{userId}
order by award_time desc
limit #{page}, #{pageSize}

select count(*)
from user_award_record
where user_id = #{userId}
